CREATE TABLE `t_employee` (`id` INT(11) NOT NULL auto_increment,`employee_no` VARCHAR(100) NOT NULL,`employee_name` VARCHAR(100) NOT NULL,`employee_years` INT(11) NOT NULL,`employee_salary` DECIMAL(100) NOT NULL,`create_time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,`update_time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,PRIMARY KEY (`id`))